  afterClosed: Observable<any>;
  afterOpened: Observable<void>;
  close: (result?: any) => void;
  updateConfig: (config: Partial<ModalConfig>) => void;
  updateSize: (size: ModalSize) => void;
  updatePosition: (position: ModalPosition) => void;
