import { Injectable, TemplateRef, ComponentRef, ViewContainerRef, Injector } from '@angular/core';
import { Overlay, OverlayRef } from '@angular/cdk/overlay';
import { ComponentPortal, TemplatePortal } from '@angular/cdk/portal';
import { Subject, Observable } from 'rxjs';
import { ModalComponent, ModalConfig, ModalSize, ModalPosition, ModalVariant } from './modal.component';

export interface ModalRef<T = any> {
  instance: ModalComponent;
  componentInstance?: T;
  overlayRef: OverlayRef;
  afterClosed: Observable<any>;
  afterOpened: Observable<void>;
  close: (result?: any) => void;
  updateConfig: (config: Partial<ModalConfig>) => void;
  updateSize: (size: ModalSize) => void;
  updatePosition: (position: ModalPosition) => void;
}

export interface ModalData {
  [key: string]: any;
}

export interface ModalOptions extends Partial<ModalConfig> {
  data?: ModalData;
  viewContainerRef?: ViewContainerRef;
  injector?: Injector;
}

@Injectable({
  providedIn: 'root'
})
export class ModalService {
  private _openModalRefs: ModalRef[] = [];
  private _globalConfig: Partial<ModalConfig> = {
    size: 'base',
    position: 'center',
    variant: 'default',
    closeOnBackdropClick: true,
    closeOnEscape: true,
    disableClose: false,
    hasBackdrop: true,
    animationEnabled: true,
    animationDuration: 300,
    autoFocus: true,
    restoreFocus: true
  };

  constructor(private overlay: Overlay) {}

  /**
   * Opens a modal with a component
   */
  open<T = any, R = any>(
    component: any,
    options?: ModalOptions
  ): ModalRef<T> {
    const config = { ...this._globalConfig, ...options };
    const overlayRef = this.createOverlay(config);
    const modalRef = this.createModalRef<T, R>(overlayRef, config);

    // Create component portal
    const componentPortal = new ComponentPortal(
      component,
      options?.viewContainerRef,
      options?.injector
    );

    // Attach component to modal
    const componentRef = overlayRef.attach(componentPortal);
    modalRef.componentInstance = componentRef.instance;

    // Pass data to component if provided
    if (options?.data) {
      Object.assign(componentRef.instance, options.data);
    }

    // Setup modal lifecycle
    this.setupModalLifecycle(modalRef);

    // Track open modal
    this._openModalRefs.push(modalRef);

    return modalRef;
  }

  /**
   * Opens a modal with a template
   */
  openTemplate<R = any>(
    template: TemplateRef<any>,
    options?: ModalOptions
  ): ModalRef<any> {
    const config = { ...this._globalConfig, ...options };
    const overlayRef = this.createOverlay(config);
    const modalRef = this.createModalRef<any, R>(overlayRef, config);

    // Create template portal
    const templatePortal = new TemplatePortal(
      template,
      options?.viewContainerRef || null,
      options?.data
    );

    // Attach template to modal
    overlayRef.attach(templatePortal);

    // Setup modal lifecycle
    this.setupModalLifecycle(modalRef);

    // Track open modal
    this._openModalRefs.push(modalRef);

    return modalRef;
  }

  /**
   * Opens a confirmation modal
   */
  openConfirm(options: {
    title?: string;
    message?: string;
    confirmText?: string;
    cancelText?: string;
    variant?: ModalVariant;
    size?: ModalSize;
  } & ModalOptions): ModalRef<any> {
    const config = {
      ...this._globalConfig,
      ...options,
      variant: options.variant || 'default',
      size: options.size || 'sm'
    };

    const overlayRef = this.createOverlay(config);
    const modalRef = this.createModalRef(overlayRef, config);

    // Create confirmation component (you would create this separately)
    // For now, we'll use a simple template approach
    const confirmationData = {
      title: options.title || 'Confirm Action',
      message: options.message || 'Are you sure you want to proceed?',
      confirmText: options.confirmText || 'Confirm',
      cancelText: options.cancelText || 'Cancel',
      onConfirm: () => modalRef.close(true),
      onCancel: () => modalRef.close(false)
    };

    // You would typically create a ConfirmationComponent here
    // For this implementation, we'll simulate it
    modalRef.componentInstance = confirmationData;

    // Setup modal lifecycle
    this.setupModalLifecycle(modalRef);

    // Track open modal
    this._openModalRefs.push(modalRef);

    return modalRef;
  }

  /**
   * Opens an alert modal
   */
  openAlert(options: {
    title?: string;
    message?: string;
    buttonText?: string;
    variant?: ModalVariant;
    size?: ModalSize;
  } & ModalOptions): ModalRef<any> {
    const config = {
      ...this._globalConfig,
      ...options,
      variant: options.variant || 'default',
      size: options.size || 'sm',
      closeOnBackdropClick: false,
      closeOnEscape: false
    };

    const overlayRef = this.createOverlay(config);
    const modalRef = this.createModalRef(overlayRef, config);

    // Create alert data
    const alertData = {
      title: options.title || 'Alert',
      message: options.message || 'This is an alert message.',
      buttonText: options.buttonText || 'OK',
      onClose: () => modalRef.close(true)
    };

    modalRef.componentInstance = alertData;

    // Setup modal lifecycle
    this.setupModalLifecycle(modalRef);

    // Track open modal
    this._openModalRefs.push(modalRef);

    return modalRef;
  }

  /**
   * Closes all open modals
   */
  closeAll(): void {
    this._openModalRefs.forEach(modalRef => {
      modalRef.close();
    });
  }

  /**
   * Gets all open modal references
   */
  getOpenModals(): ModalRef[] {
    return [...this._openModalRefs];
  }

  /**
   * Checks if any modal is currently open
   */
  hasOpenModals(): boolean {
    return this._openModalRefs.length > 0;
  }

  /**
   * Sets global configuration for all modals
   */
  setGlobalConfig(config: Partial<ModalConfig>): void {
    this._globalConfig = { ...this._globalConfig, ...config };
  }

  /**
   * Gets the current global configuration
   */
  getGlobalConfig(): Partial<ModalConfig> {
    return { ...this._globalConfig };
  }

  private createOverlay(config: Partial<ModalConfig>): OverlayRef {
    const overlayConfig = {
      hasBackdrop: config.hasBackdrop !== false,
      backdropClass: ['modal-backdrop', config.backdropClass].filter(Boolean),
      panelClass: ['modal-overlay-panel', config.panelClass].filter(Boolean),
      scrollStrategy: this.overlay.scrollStrategies.block(),
      positionStrategy: this.overlay.position().global()
    };

    // Configure position strategy
    switch (config.position) {
      case 'top':
        overlayConfig.positionStrategy.top('5%').centerHorizontally();
        break;
      case 'bottom':
        overlayConfig.positionStrategy.bottom('5%').centerHorizontally();
        break;
      default:
        overlayConfig.positionStrategy.centerHorizontally().centerVertically();
        break;
    }

    return this.overlay.create(overlayConfig);
  }

  private createModalRef<T, R>(
    overlayRef: OverlayRef,
    config: Partial<ModalConfig>
  ): ModalRef<T> {
    const afterClosed = new Subject<R>();
    const afterOpened = new Subject<void>();

    const modalRef: ModalRef<T> = {
      instance: null as any, // Will be set after component creation
      overlayRef,
      afterClosed: afterClosed.asObservable(),
      afterOpened: afterOpened.asObservable(),
      close: (result?: R) => {
        this.closeModal(modalRef, result);
      },
      updateConfig: (newConfig: Partial<ModalConfig>) => {
        if (modalRef.instance) {
          modalRef.instance.updateConfig(newConfig);
        }
      },
      updateSize: (size: ModalSize) => {
        if (modalRef.instance) {
          modalRef.instance.size = size;
        }
      },
      updatePosition: (position: ModalPosition) => {
        if (modalRef.instance) {
          modalRef.instance.position = position;
        }
      }
    };

    return modalRef;
  }

  private setupModalLifecycle<T>(modalRef: ModalRef<T>): void {
    // Handle backdrop clicks
    if (modalRef.overlayRef.backdropClick) {
      modalRef.overlayRef.backdropClick().subscribe(() => {
        if (modalRef.instance && modalRef.instance.closeOnBackdropClick) {
          modalRef.close();
        }
      });
    }

    // Handle escape key
    if (modalRef.overlayRef.keydownEvents) {
      modalRef.overlayRef.keydownEvents().subscribe(event => {
        if (event.keyCode === 27 && modalRef.instance && modalRef.instance.closeOnEscape) {
          modalRef.close();
        }
      });
    }

    // Emit opened event
    setTimeout(() => {
      (modalRef.afterOpened as Subject<void>).next();
    });
  }

  private closeModal<T>(modalRef: ModalRef<T>, result?: any): void {
    // Remove from open modals list
    const index = this._openModalRefs.indexOf(modalRef);
    if (index > -1) {
      this._openModalRefs.splice(index, 1);
    }

    // Emit closed event
    (modalRef.afterClosed as Subject<any>).next(result);
    (modalRef.afterClosed as Subject<any>).complete();

    // Dispose overlay
    if (modalRef.overlayRef) {
      modalRef.overlayRef.dispose();
    }
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { StepConfig } from './progress-steps.types';

@Injectable({
  providedIn: 'root'
})
export class ProgressStepsService {
  private stepsSubject = new BehaviorSubject<StepConfig[]>([]);
  private currentStepSubject = new BehaviorSubject<number>(0);
  private allowNavigationSubject = new BehaviorSubject<boolean>(false);

  steps$ = this.stepsSubject.asObservable();
  currentStep$ = this.currentStepSubject.asObservable();
  allowNavigation$ = this.allowNavigationSubject.asObservable();

  get steps(): StepConfig[] {
    return this.stepsSubject.value;
  }

  get currentStep(): number {
    return this.currentStepSubject.value;
  }

  get allowNavigation(): boolean {
    return this.allowNavigationSubject.value;
  }

  setSteps(steps: StepConfig[]): void {
    this.stepsSubject.next([...steps]);
  }

  setAllowNavigation(allow: boolean): void {
    this.allowNavigationSubject.next(allow);
  }

  goToStep(index: number): boolean {
    const steps = this.stepsSubject.value;

    if (index < 0 || index >= steps.length) {
      return false;
    }

    const targetStep = steps[index];
    if (targetStep.disabled) {
      return false;
    }

    // If navigation is not allowed, only allow forward to next step or backward if completed
    if (!this.allowNavigation) {
      const currentIndex = this.currentStepSubject.value;

      // Allow forward to next step only
      if (index > currentIndex && index !== currentIndex + 1) {
        return false;
      }

      // Allow backward navigation only to completed steps
      if (index < currentIndex && !steps[index].completed) {
        return false;
      }
    }

    this.currentStepSubject.next(index);
    return true;
  }

  nextStep(): boolean {
    const current = this.currentStepSubject.value;
    const steps = this.stepsSubject.value;
    let nextIndex = current + 1;

    // Skip optional steps if not explicitly navigated to
    while (nextIndex < steps.length && steps[nextIndex].disabled) {
      nextIndex++;
    }

    if (nextIndex < steps.length) {
      return this.goToStep(nextIndex);
    }

    return false;
  }

  previousStep(): boolean {
    const current = this.currentStepSubject.value;
    let prevIndex = current - 1;

    // Skip disabled steps when going backward
    while (prevIndex >= 0 && this.steps[prevIndex].disabled) {
      prevIndex--;
    }

    if (prevIndex >= 0) {
      return this.goToStep(prevIndex);
    }

    return false;
  }

  markStepCompleted(index: number): void {
    const steps = [...this.stepsSubject.value];
    if (steps[index]) {
      steps[index].completed = true;
      steps[index].error = false;
      this.setSteps(steps);
    }
  }

  markStepError(index: number): void {
    const steps = [...this.stepsSubject.value];
    if (steps[index]) {
      steps[index].error = true;
      steps[index].completed = false;
      this.setSteps(steps);
    }
  }

  markStepPending(index: number): void {
    const steps = [...this.stepsSubject.value];
    if (steps[index]) {
      steps[index].error = false;
      steps[index].completed = false;
      this.setSteps(steps);
    }
  }

  isStepAccessible(index: number): boolean {
    const steps = this.stepsSubject.value;
    const step = steps[index];

    if (!step || step.disabled) {
      return false;
    }

    if (!this.allowNavigation) {
      const currentIndex = this.currentStepSubject.value;

      // Can access current step
      if (index === currentIndex) {
        return true;
      }

      // Can access next step
      if (index === currentIndex + 1) {
        return true;
      }

      // Can access completed previous steps
      if (index < currentIndex && step.completed) {
        return true;
      }

      return false;
    }

    return true;
  }

  getProgress(): number {
    const steps = this.stepsSubject.value;
    if (steps.length === 0) return 0;

    const completedSteps = steps.filter(step => step.completed).length;
    return Math.round((completedSteps / steps.length) * 100);
  }

  reset(): void {
    const steps = this.stepsSubject.value.map(step => ({
      ...step,
      completed: false,
      error: false
    }));
    this.setSteps(steps);
    this.currentStepSubject.next(0);
  }

  hasErrors(): boolean {
    return this.stepsSubject.value.some(step => step.error);
  }

  canProceed(): boolean {
    const current = this.currentStepSubject.value;
    const steps = this.stepsSubject.value;

    if (current >= steps.length) return false;

    const currentStep = steps[current];
    return !currentStep.error && !currentStep.disabled;
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable, Subject, debounceTime, distinctUntilChanged, map } from 'rxjs';
import { 
  SelectorOption, 
  SelectorGroup, 
  SelectorState, 
  SelectorConfig, 
  SelectorMode,
  SelectorAsyncConfig,
  SelectorHighlight 
} from './selector.types';

@Injectable()
export class SelectorService {
  private stateSubject = new BehaviorSubject<SelectorState>({
    selectedOptions: [],
    selectedValues: [],
    searchTerm: '',
    filteredOptions: [],
    filteredGroups: [],
    isSearching: false,
    hasError: false
  });

  private searchSubject = new Subject<string>();
  public state$ = this.stateSubject.asObservable();

  // Store original data
  private originalOptions: SelectorOption[] = [];
  private originalGroups: SelectorGroup[] = [];

  constructor() {
    // Setup search debouncing
    this.searchSubject.pipe(
      debounceTime(300),
      distinctUntilChanged()
    ).subscribe(searchTerm => {
      this.performSearch(searchTerm);
    });
  }

  /**
   * Initialize the service with options and configuration
   */
  initialize(
    options: SelectorOption[], 
    groups: SelectorGroup[] = [], 
    config: SelectorConfig,
    asyncConfig?: SelectorAsyncConfig
  ): void {
    // Store original data
    this.originalOptions = [...options];
    this.originalGroups = [...groups];

    const initialState: SelectorState = {
      ...this.stateSubject.value,
      filteredOptions: options,
      filteredGroups: groups,
      searchTerm: '',
      isSearching: false,
      hasError: false
    };

    this.stateSubject.next(initialState);
  }

  /**
   * Update search term and trigger filtering
   */
  updateSearch(searchTerm: string): void {
    const currentState = this.stateSubject.value;
    this.stateSubject.next({
      ...currentState,
      searchTerm,
      isSearching: true
    });

    this.searchSubject.next(searchTerm);
  }

  /**
   * Perform the actual search filtering
   */
  private performSearch(searchTerm: string): void {
    const currentState = this.stateSubject.value;

    if (!searchTerm.trim()) {
      this.stateSubject.next({
        ...currentState,
        isSearching: false,
        filteredOptions: this.getAllOptions(),
        filteredGroups: this.getAllGroups()
      });
      return;
    }

    const filteredOptions = this.filterOptions(this.getAllOptions(), searchTerm);
    const filteredGroups = this.filterGroups(this.getAllGroups(), searchTerm);

    this.stateSubject.next({
      ...currentState,
      filteredOptions,
      filteredGroups,
      isSearching: false
    });
  }

  /**
   * Filter options based on search term
   */
  private filterOptions(options: SelectorOption[], searchTerm: string): SelectorOption[] {
    const term = searchTerm.toLowerCase();
    return options.filter(option => 
      option.label.toLowerCase().includes(term) ||
      (option.description && option.description.toLowerCase().includes(term)) ||
      (option.badge && option.badge.toLowerCase().includes(term))
    );
  }

  /**
   * Filter groups and their options based on search term
   */
  private filterGroups(groups: SelectorGroup[], searchTerm: string): SelectorGroup[] {
    const term = searchTerm.toLowerCase();

    return groups
      .map(group => ({
        ...group,
        options: this.filterOptions(group.options, searchTerm)
      }))
      .filter(group => 
        group.label.toLowerCase().includes(term) ||
        group.options.length > 0
      );
  }

  /**
   * Select an option
   */
  selectOption(option: SelectorOption, mode: SelectorMode): void {
    const currentState = this.stateSubject.value;
    let selectedOptions: SelectorOption[];
    let selectedValues: any[];

    if (mode === 'single') {
      selectedOptions = [option];
      selectedValues = [option.value];
    } else {
      // Multiple selection
      if (this.isOptionSelected(option)) {
        // Deselect option
        selectedOptions = currentState.selectedOptions.filter(opt => opt.id !== option.id);
        selectedValues = currentState.selectedValues.filter(val => val !== option.value);
      } else {
        // Select option
        selectedOptions = [...currentState.selectedOptions, option];
        selectedValues = [...currentState.selectedValues, option.value];
      }
    }

    this.stateSubject.next({
      ...currentState,
      selectedOptions,
      selectedValues
    });
  }

  /**
   * Deselect an option
   */
  deselectOption(option: SelectorOption): void {
    const currentState = this.stateSubject.value;
    const selectedOptions = currentState.selectedOptions.filter(opt => opt.id !== option.id);
    const selectedValues = currentState.selectedValues.filter(val => val !== option.value);

    this.stateSubject.next({
      ...currentState,
      selectedOptions,
      selectedValues
    });
  }

  /**
   * Clear all selections
   */
  clearSelection(): void {
    const currentState = this.stateSubject.value;
    this.stateSubject.next({
      ...currentState,
      selectedOptions: [],
      selectedValues: []
    });
  }

  /**
   * Set selected values programmatically
   */
  setSelectedValues(values: any[]): void {
    const currentState = this.stateSubject.value;
    const allOptions = [...this.getAllOptions(), ...this.getAllGroupOptions()];
    const selectedOptions = allOptions.filter(option => values.includes(option.value));

    this.stateSubject.next({
      ...currentState,
      selectedOptions,
      selectedValues: values
    });
  }

  /**
   * Check if an option is selected
   */
  isOptionSelected(option: SelectorOption): boolean {
    const currentState = this.stateSubject.value;
    return currentState.selectedOptions.some(opt => opt.id === option.id);
  }

  /**
   * Toggle group collapse state
   */
  toggleGroup(groupId: string): void {
    // This would be implemented if groups are managed by the service
    // For now, groups are managed by the component
  }

  /**
   * Validate current selection
   */
  validateSelection(config: SelectorConfig): string | null {
    const currentState = this.stateSubject.value;
    const selectedCount = currentState.selectedOptions.length;

    if (config.validation) {
      const { required, minSelection, maxSelection, customValidator } = config.validation;

      if (required && selectedCount === 0) {
        return 'At least one option must be selected';
      }

      if (minSelection && selectedCount < minSelection) {
        return `At least ${minSelection} option${minSelection > 1 ? 's' : ''} must be selected`;
      }

      if (maxSelection && selectedCount > maxSelection) {
        return `At most ${maxSelection} option${maxSelection > 1 ? 's' : ''} can be selected`;
      }

      if (customValidator) {
        return customValidator(currentState.selectedValues);
      }
    }

    return null;
  }

  /**
   * Set error state
   */
  setError(errorMessage: string | null): void {
    const currentState = this.stateSubject.value;
    this.stateSubject.next({
      ...currentState,
      hasError: !!errorMessage,
      errorMessage: errorMessage || undefined
    });
  }

  /**
   * Highlight search terms in text
   */
  highlightSearchTerm(text: string, searchTerm: string, config: SelectorHighlight): string {
    if (!config.enabled || !searchTerm.trim()) {
      return text;
    }

    const className = config.className || 'selector-highlight';
    const flags = config.caseSensitive ? 'g' : 'gi';
    const pattern = config.wholeWord 
      ? new RegExp(`\\b${this.escapeRegExp(searchTerm)}\\b`, flags)
      : new RegExp(this.escapeRegExp(searchTerm), flags);

    return text.replace(pattern, `<span class="${className}">$&</span>`);
  }

  /**
   * Get keyboard navigation suggestions
   */
  getNavigationOptions(): SelectorOption[] {
    const currentState = this.stateSubject.value;
    return currentState.filteredOptions.filter(option => !option.disabled);
  }

  /**
   * Get current state snapshot
   */
  getCurrentState(): SelectorState {
    return this.stateSubject.value;
  }

  /**
   * Reset service state
   */
  reset(): void {
    this.stateSubject.next({
      selectedOptions: [],
      selectedValues: [],
      searchTerm: '',
      filteredOptions: [],
      filteredGroups: [],
      isSearching: false,
      hasError: false
    });
  }

  /**
   * Helper methods
   */
  private getAllOptions(): SelectorOption[] {
    return this.originalOptions;
  }

  private getAllGroups(): SelectorGroup[] {
    return this.originalGroups;
  }

  private getAllGroupOptions(): SelectorOption[] {
    return this.getAllGroups().reduce((acc, group) => [...acc, ...group.options], [] as SelectorOption[]);
  }

  private escapeRegExp(string: string): string {
    return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
  }
}

