/* Form Component Styles */

.form-container {
  width: 100%;
  max-width: 100%;
}

.form {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-6);
  width: 100%;

  // Size variants
  &.size-sm {
    gap: var(--spacing-4);

    .form-content {
      gap: var(--spacing-4);
    }

    .section-fields,
    .form-fields {
      gap: var(--spacing-3);
    }
  }

  &.size-base {
    gap: var(--spacing-6);

    .form-content {
      gap: var(--spacing-6);
    }

    .section-fields,
    .form-fields {
      gap: var(--spacing-4);
    }
  }

  &.size-lg {
    gap: var(--spacing-8);

    .form-content {
      gap: var(--spacing-8);
    }

    .section-fields,
    .form-fields {
      gap: var(--spacing-6);
    }
  }

  // Submit state styling
  &.submit-loading {
    pointer-events: none;
    opacity: 0.7;
  }

  &.submit-error {
    .form-actions {
      border-top: 2px solid var(--color-error-300);
    }
  }

  &.submit-success {
    .form-actions {
      border-top: 2px solid var(--color-success-300);
    }
  }

  &.form-invalid {
    .form-actions {
      border-top: 1px solid var(--color-error-300);
    }
  }
}

/* Form Content Layout */
.form-content {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-6);
  width: 100%;
}

/* Field Layout Variants */
.section-fields,
.form-fields {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);

  // Single column layout (default)
  &.layout-single {
    display: flex;
    flex-direction: column;
  }

  // Two column layout
  &.layout-two-column {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--spacing-4) var(--spacing-6);
    align-items: start;

    // Full width elements
    .full-width,
    .form-section,
    .form-actions {
      grid-column: 1 / -1;
    }
  }

  // Grid layout
  &.layout-grid {
    display: grid;
    gap: var(--spacing-4) var(--spacing-6);
    align-items: start;

    // Grid columns based on size
    .form.size-sm & {
      grid-template-columns: 1fr;
    }

    .form.size-base & {
      grid-template-columns: repeat(2, 1fr);
    }

    .form.size-lg & {
      grid-template-columns: repeat(3, 1fr);
    }

    // Full width elements
    .full-width,
    .form-section,
    .form-actions {
      grid-column: 1 / -1;
    }
  }

  // Inline layout
  &.layout-inline {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    align-items: end;
    gap: var(--spacing-4);

    // Make form controls inline
    ::ng-deep .container {
      flex: 1;
      min-width: 200px;
    }
  }
}

/* Form Sections */
.form-section {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
  width: 100%;

  &.collapsible {
    border-radius: var(--radius-md);
    border: 1px solid var(--color-border-primary);
    overflow: hidden;

    .section-header {
      cursor: pointer;
      transition: background-color var(--animation-duration-base) var(--animation-ease-in-out);

      &:hover {
        background-color: var(--color-background-secondary);
      }

      &:focus-within {
        outline: 2px solid var(--color-primary-600);
        outline-offset: -2px;
      }
    }
  }

  &.collapsed {
    .section-content {
      display: none;
    }

    .toggle-icon {
      transform: rotate(0deg);
    }
  }
}

/* Section Header */
.section-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-4) var(--spacing-5);

  &.clickable {
    user-select: none;

    &:focus {
      outline: 2px solid var(--color-primary-600);
      outline-offset: -2px;
    }
  }
}

.section-header-content {
  flex: 1;
}

.section-title {
  font-size: var(--font-size-lg);
  font-weight: var(--font-weight-semibold);
  color: var(--color-text-primary);
  margin: 0 0 var(--spacing-1) 0;

  .required-indicator {
    color: var(--color-error-500);
    margin-left: var(--spacing-1);
  }
}

.section-description {
  font-size: var(--font-size-sm);
  color: var(--color-text-secondary);
  margin: 0;
  line-height: 1.5;
}

.section-toggle {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--spacing-1);

  .toggle-icon {
    transition: transform var(--animation-duration-base) var(--animation-ease-in-out);
    color: var(--color-text-secondary);

    &.rotated {
      transform: rotate(90deg);
    }
  }
}

/* Section Content */
.section-content {
  padding: 0 var(--spacing-5) var(--spacing-5);

  &.collapsed {
    display: none;
  }
}

/* Form Actions */
.form-actions {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-4);
  padding-top: var(--spacing-6);
  border-top: 1px solid var(--color-border-primary);
  margin-top: var(--spacing-6);
}

/* Form Messages */
.form-messages {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-3);
}

.error-message,
.success-message {
  display: flex;
  align-items: center;
  gap: var(--spacing-2);
  padding: var(--spacing-3) var(--spacing-4);
  border-radius: var(--radius-md);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);

  .message-icon {
    flex-shrink: 0;
  }
}

.error-message {
  background-color: var(--color-error-100);
  color: var(--color-error-700);
  border: 1px solid var(--color-error-300);

  .message-icon {
    color: var(--color-error-500);
  }
}

.success-message {
  background-color: var(--color-success-100);
  color: var(--color-success-700);
  border: 1px solid var(--color-success-300);

  .message-icon {
    color: var(--color-success-500);
  }
}

/* Action Buttons */
.action-buttons {
  display: flex;
  gap: var(--spacing-3);
  justify-content: flex-end;
  flex-wrap: wrap;

  .button {
    // Button styles are handled by the button component
    // This ensures consistent button styling
  }
}

/* Loading States */
.loading-spinner {
  animation: spin 1s linear infinite;
  margin-right: var(--spacing-2);
}

@keyframes spin {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.button-content {
  display: flex;
  align-items: center;
  justify-content: center;

  &.loading {
    .loading-spinner {
      margin-right: var(--spacing-2);
    }
  }
}

/* Responsive Design */
@media (max-width: 768px) {
  .form {
    gap: var(--spacing-4);

    .form-content {
      gap: var(--spacing-4);
    }
  }

  .section-fields,
  .form-fields {
    // Force single column on mobile
    &.layout-two-column,
    &.layout-grid {
      grid-template-columns: 1fr !important;
      gap: var(--spacing-3);
    }

    &.layout-inline {
      flex-direction: column;
      align-items: stretch;

      ::ng-deep .container {
        min-width: unset;
      }
    }
  }

  .section-header {
    padding: var(--spacing-3) var(--spacing-4);
  }

  .section-content {
    padding: 0 var(--spacing-4) var(--spacing-4);
  }

  .section-title {
    font-size: var(--font-size-base);
  }

  .action-buttons {
    flex-direction: column-reverse;

    .button {
      width: 100%;
    }
  }

  .form-actions {
    margin-top: var(--spacing-4);
    padding-top: var(--spacing-4);
  }
}

@media (max-width: 480px) {
  .form.size-lg {
    gap: var(--spacing-4);

    .form-content {
      gap: var(--spacing-4);
    }

    .section-fields,
    .form-fields {
      gap: var(--spacing-3);
    }
  }
}

/* Focus Management */
.form {
  // Ensure proper focus order and accessibility
  *:focus {
    outline: 2px solid var(--color-primary-600);
    outline-offset: 2px;
  }

  // Focus trap for sections
  .section-content:focus-within {
    .section-header {
      background-color: var(--color-background-secondary);
    }
  }
}

/* High Contrast Mode Support */
@media (prefers-contrast: high) {
  .form-section.collapsible {
    border-width: 2px;
  }

  .error-message,
  .success-message {
    border-width: 2px;
  }

  .form-actions {
    border-top-width: 2px;
  }
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
  .toggle-icon,
  .section-header,
  .loading-spinner {
    transition: none;
    animation: none;
  }
}

/* Print Styles */
@media print {
  .form {
    background: white;
    color: black;
  }

  .form-actions,
  .action-buttons {
    display: none;
  }

  .section-content.collapsed {
    display: block !important;
  }

  .toggle-icon {
    display: none;
  }
}

