/* Input Component Styles - Exact match with React version */

/* Container */
.container {
  width: 100%;
  display: block;
}

/* Label */
.label {
  display: block;
  font-family: var(--font-family-base);
  font-size: 14px;
  font-weight: 500;
  color: var(--color-text-primary);
  margin-bottom: 8px;
  line-height: 1.25;
}

.required {
  color: var(--color-error-500);
  margin-left: 4px;
}

/* Input Wrapper */
.inputWrapper {
  position: relative;
  display: flex;
  align-items: center;
}

/* Base Input Styles */
.input {
  width: 100%;
  border: 1px solid var(--color-border-primary);
  border-radius: 8px;
  background-color: var(--color-background-primary);
  color: var(--color-text-primary);
  font-family: var(--font-family-base);
  font-size: 14px;
  line-height: 1.5;
  transition: all 200ms cubic-bezier(0.4, 0, 0.2, 1);
  outline: none;
  box-sizing: border-box;
}

.input::placeholder {
  color: var(--color-text-placeholder);
}

/* Size Variants */
.input.sm {
  height: 32px;
  padding: 0 12px;
  font-size: 12px;
}

.input.sm.hasStartIcon {
  padding-left: 32px;
}

.input.sm.hasEndIcon {
  padding-right: 32px;
}

.input.base {
  height: 40px;
  padding: 0 16px;
  font-size: 14px;
}

.input.base.hasStartIcon {
  padding-left: 40px;
}

.input.base.hasEndIcon {
  padding-right: 40px;
}

.input.lg {
  height: 48px;
  padding: 0 20px;
  font-size: 16px;
}

.input.lg.hasStartIcon {
  padding-left: 48px;
}

.input.lg.hasEndIcon {
  padding-right: 48px;
}

/* Style Variants */
.input.default {
  border: 1px solid var(--color-border-primary);
  background-color: var(--color-background-primary);
}

.input.filled {
  background-color: var(--color-background-secondary);
  border-color: transparent;
}

.input.filled:hover:not(:disabled) {
  background-color: var(--color-background-tertiary);
}

.input.filled:focus {
  background-color: var(--color-background-primary);
  border-color: var(--color-primary-600);
}

.input.ghost {
  background-color: transparent;
  border-color: transparent;
  padding-left: 0;
  padding-right: 0;
}

/* Ghost variant icon padding overrides */
.input.ghost.hasStartIcon {
  padding-left: 24px;
}

.input.ghost.hasEndIcon {
  padding-right: 24px;
}

.input.ghost:hover:not(:disabled) {
  background-color: var(--color-background-secondary);
  border-color: var(--color-border-primary);
}

.input.ghost:focus {
  background-color: var(--color-background-primary);
  border-color: var(--color-primary-600);
}

/* Interactive States */
.input:hover:not(:disabled) {
  border-color: var(--color-border-hover);
}

.input:focus {
  border-color: var(--color-primary-600);
  box-shadow: var(--shadow-focus);
}

/* Error State */
.input.error {
  border-color: var(--color-error-300);
  background-color: var(--color-background-primary);
}

.input.error:focus {
  border-color: var(--color-error-500);
  box-shadow: var(--shadow-error-focus);
}

/* Success State */
.input.success {
  border-color: var(--color-success-300);
}

.input.success:focus {
  border-color: var(--color-success-500);
  box-shadow: 0 0 0 4px var(--color-success-100);
}

/* Disabled State */
.input:disabled {
  background-color: var(--color-background-disabled);
  border-color: var(--color-border-disabled);
  color: var(--color-text-disabled);
  cursor: not-allowed;
}

/* Icon Containers */
.startIcon,
.endIcon {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-text-tertiary);
  pointer-events: none;
  z-index: 1;
}

.startIcon {
  left: 12px;
}

.endIcon {
  right: 12px;
}

/* Icon Size */
.startIcon svg,
.endIcon svg {
  width: 16px;
  height: 16px;
  flex-shrink: 0;
}

/* Help Text Container */
.helpText {
  margin-top: 8px;
  font-size: 12px;
  line-height: 1.25;
}

/* Helper Text */
.helperText {
  color: var(--color-text-secondary);
}

/* Error Text */
.errorText {
  color: var(--color-text-error);
  display: flex;
  align-items: center;
  gap: 4px;
}

.errorText svg {
  width: 12px;
  height: 12px;
  flex-shrink: 0;
}

/* Focus management for labels */
.label:focus-within {
  color: var(--color-primary-600);
}

/* Enhanced accessibility for disabled state */
.input:disabled + .startIcon,
.input:disabled + .endIcon {
  color: var(--color-text-disabled);
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .input.lg {
    height: 44px;
    font-size: 15px;
  }

  .input.base {
    height: 38px;
  }

  .input.sm {
    height: 30px;
  }
}

