/* ProgressSteps Component Styles */

.progress-steps {
  width: 100%;
  font-family: var(--font-family-base);
}

.steps-container {
  display: flex;
  align-items: flex-start;
  position: relative;
  width: 100%;
  justify-content: space-between;
}

.step-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  flex: 1;
  max-width: 200px;
}

.step-item.clickable {
  cursor: pointer;
}

.step-circle {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  border: 2px solid var(--color-border-primary);
  background: var(--color-background-primary);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 2;
  transition: all var(--animation-duration-base) var(--animation-ease-in-out);
}

.step-number {
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-semibold);
  color: var(--color-text-secondary);
}

.step-icon {
  font-size: 16px;
  font-weight: bold;
  color: white;
}

/* Step States */
.step-item.completed .step-circle {
  background: var(--color-success-600);
  border-color: var(--color-success-600);
}

.step-item.current .step-circle {
  background: var(--color-primary-600);
  border-color: var(--color-primary-600);
  box-shadow: var(--shadow-focus);
}

.step-item.current .step-number {
  color: white;
}

.step-item.error .step-circle {
  background: var(--color-error-600);
  border-color: var(--color-error-600);
}

.step-item.disabled .step-circle {
  background: var(--color-background-disabled);
  border-color: var(--color-border-disabled);
  opacity: 0.6;
}

.step-item.disabled .step-number {
  color: var(--color-text-disabled);
}

/* Step Content */
.step-content {
  margin-top: var(--spacing-3);
  text-align: center;
  max-width: 120px;
}

.step-label {
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);
  color: var(--color-text-primary);
  line-height: 1.4;
  margin-bottom: var(--spacing-1);
}

.step-description {
  font-size: var(--font-size-xs);
  color: var(--color-text-secondary);
  line-height: 1.4;
}

.step-optional {
  font-size: var(--font-size-xs);
  color: var(--color-text-tertiary);
  font-weight: normal;
}

/* Connector Lines */
.step-connector {
  position: absolute;
  top: 20px;
  left: 50%;
  width: 100%;
  height: 2px;
  background: var(--color-border-primary);
  z-index: 1;
}

.step-item.completed .step-connector {
  background: var(--color-success-600);
}

/* Hover Effects */
.step-item.clickable:hover .step-circle {
  transform: scale(1.05);
}

.step-item.clickable:hover .step-label {
  color: var(--color-primary-600);
}

/* Responsive */
@media (max-width: 768px) {
  .step-content {
    max-width: 80px;
  }

  .step-label {
    font-size: var(--font-size-xs);
  }

  .step-description {
    font-size: 10px;
  }

  .step-circle {
    width: 32px;
    height: 32px;
  }

  .step-number {
    font-size: 12px;
  }

  .step-icon {
    font-size: 14px;
  }

  .step-connector {
    top: 16px;
  }
}

