import React from 'react';
import { Box, Button, Heading, Text } from '@vibe/core';

interface SimpleCardProps {
  title?: string;
  description?: string;
  buttonText?: string;
  onButtonClick?: () => void;
}

export const SimpleCard: React.FC<SimpleCardProps> = ({
  title = "Welcome to Vibe",
  description = "This is a simple card component built with the Vibe design system. It demonstrates the use of Box, Heading, Text, and Button components working together.",
  buttonText = "Get Started",
  onButtonClick = () => console.log("Button clicked!")
}) => {
  return (
    <Box
      border
      rounded="medium"
      shadow="medium"
      padding="large"
      backgroundColor="primaryBackgroundColor"
      style={{ maxWidth: '400px', margin: '20px auto' }}
    >
      <Heading type="h2" weight="medium" color="primary">
        {title}
      </Heading>

      <Text 
        type="text2" 
        color="secondary" 
        style={{ marginTop: '12px', marginBottom: '20px' }}
      >
        {description}
      </Text>

      <Button
        kind="primary"
        size="medium"
        onClick={onButtonClick}
        style={{ width: '100%' }}
      >
        {buttonText}
      </Button>
    </Box>
  );
};

export default SimpleCard;
