import React, { useState } from 'react';
import { Box, Button, Heading, Text, Flex, Badge, Avatar } from '@vibe/core';
import { Modal, ModalHeader, ModalContent, ModalFooter } from '@vibe/core/next';

interface SimpleCardProps {
  title?: string;
  description?: string;
  buttonText?: string;
  onButtonClick?: () => void;
  type?: 'primary' | 'success' | 'warning' | 'danger';
  showBadge?: boolean;
  badgeText?: string;
}

export const SimpleCard: React.FC<SimpleCardProps> = ({
  title = "Welcome to Vibe",
  description = "This is a modern card component built with the Vibe design system. It demonstrates proper use of Flex, Box, Typography, and interactive elements with contemporary styling.",
  buttonText = "Get Started",
  onButtonClick = () => console.log("Button clicked!"),
  type = "primary",
  showBadge = true,
  badgeText = "New"
}) => {
  return (
    <Box
      border
      rounded="medium"
      shadow="large"
      padding="large"
      backgroundColor="primaryBackgroundColor"
      style={{ 
        maxWidth: '400px', 
        margin: '20px auto',
        transition: 'all 0.2s ease-in-out',
        cursor: 'pointer'
      }}
      className="hover:shadow-xl"
    >
      {/* Header with Badge */}
      <Flex justify="space-between" align="center" style={{ marginBottom: '16px' }}>
        <Flex align="center" gap="small">
          <Avatar 
            size="small" 
            backgroundColor="brand"
            text="V"
          />
          <Heading type="h3" weight="medium" color="primary">
            {title}
          </Heading>
        </Flex>
        {showBadge && (
          <Badge 
            text={badgeText}
            color={type === 'primary' ? 'positive' : type}
            type="indicator"
          />
        )}
      </Flex>

      {/* Content */}
      <Text 
        type="text2" 
        color="secondary" 
        style={{ marginBottom: '24px', lineHeight: '1.5' }}
      >
        {description}
      </Text>

      {/* Action Area */}
      <Flex gap="small" direction="column">
        <Button
          kind="primary"
          size="medium"
          onClick={onButtonClick}
          style={{ width: '100%' }}
        >
          {buttonText}
        </Button>

        <Flex gap="small" justify="center">
          <Button
            kind="tertiary"
            size="small"
            onClick={() => console.log("Learn more clicked")}
          >
            Learn More
          </Button>
          <Button
            kind="tertiary"
            size="small"
            onClick={() => console.log("Share clicked")}
          >
            Share
          </Button>
        </Flex>
      </Flex>
    </Box>
  );
};

export default SimpleCard;
